<?phpnamespace App\Traits;use App\Http\Libraries\Uploader;trait Upload {        private function handleImage($request, &$data, $directory, $inputName = 'image') {        $response = ['status' => 0, 'message' => 'Please upload a file'];        if ($request->hasFile($inputName)) {            $uploader = new Uploader($inputName);            if ($uploader->isValidFile()) {                $uploader->upload($directory, $uploader->fileName);                if ($uploader->isUploaded()) {                    $data[$inputName] = $uploader->getUploadedPath();                    $response['status'] = 1;                    $response['message'] = 'Uploaded';                    $response['mime_type'] = $uploader->getMimeType();                }            }            if (!$uploader->isUploaded()) {                $response = ['message' => $uploader->getMessage(), 'status' => -1];            }        }        return $response;    }    }